const movies = [
    {title: "Metrix" , year: 2001 , img:"data:image/jpeg;base64," },
    {title: "Lord of the rings", year: 2005 , img:"data:image/jpeg;base64," },
    {title: "Fight Club", year: 2003 , img:"data:image/jpeg;base64," },
    {title: "Tweleve monkeys", year: 1998 , img:"data:image/jpeg;base64," },
    {title: "Givat Halfon Eina Ona", year: 1980 , img: "https://www.google.com/imgres?imgurl=https%3A%2F%2Fm.media-amazon.com%2Fimages%2FM%2FMV5BMTU0MjEwNzYxM15BMl5BanBnXkFtZTcwNDE3NTAxMw%40%40._V1_FMjpg_UX1000_.jpg&imgrefurl=https%3A%2F%2Fwww.imdb.com%2Ftitle%2Ftt0073052%2F&tbnid=DW3OoDIQxi028M&vet=12ahUKEwjWptq7zv_1AhVNrosKHXkHDGcQMygAegQIARB-..i&docid=2YETv7yiSdQ-SM&w=1000&h=1408&q=givat%20halfon&ved=2ahUKEwjWptq7zv_1AhVNrosKHXkHDGcQMygAegQIARB-"},
];

function handleSearch(ev){
    const searchTerm = ev.target.value;
    const regex = new RegExp(searchTerm , "i");
    const root = document.querySelector("#list")
    let html = "";
    if(searchTerm == 0) return;
    movies.forEach(movie => {
        if(regex.test(movie.title)) {
            html += `<h1>title: ${movie.title} </h1>
                    <img src="${movie.img}">
                    <h2>year: ${movie.year}</h2>`
        }
    });

    root.innerHTML = html;
    
}
